import React from 'react';
import { Button, Checkbox, Form, Input, Radio, Select, TextArea } from 'semantic-ui-react'


const formValid = formErrors => {
  let valid = true;

  // make sure everything in the form is filled!
  Object.values(formErrors).forEach(val => {
    val.length > 0 && (valid = false);
  });


  return valid;
};

class SearchBar extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      term: "",
      firstName: null,
      lastName: null,
      email: null,
      password: null,
      formErrors: {
        firstName: "",
        lastName: "",
        email: "",
        password: ""
      }
    };
  }

  onFormSubmit = e => {
    e.preventDefault();

    this.props.onSubmit(this.state.term);
  };

  onInputChange = e =>{
    e.preventDefault();
    const { name, value } = e.target;
    let formErrors = { ...this.state.formErrors };
    this.setState({[name]: value});
  }

  render() {
    console.log(this.state);
    return (
      <div className = "container">
        <Form onSubmit = {this.onFormSubmit}>
          <div className="ui segment">
            <p className = "title">Feedback</p>
            <Form.Input name = "firstName" fluid placeholder='First name' onChange={this.onInputChange}/>
            <Form.Input name = "lastName" fluid placeholder='Last name'onChange={this.onInputChange}/>
            <Form.Input name = "email" fluid placeholder='john@example.com' onChange={this.onInputChange}/>
            <Form.Field control={TextArea} placeholder='Message'  onChange={e => this.setState({term: e.target.value})} maxLength={500} />
            <p>{this.state.term.length}/500</p>
            <button className="ui button">Click Here</button>
          </div>
        </Form>

      </div>
    );
  }
}

export default SearchBar;
