import React from 'react';
import { Button, Checkbox, Form, Input, Radio, Select, TextArea } from 'semantic-ui-react'

class SearchBar extends React.Component {
  state = { term: '' }; //term represents the data from the text field!

  onFormSubmit = event => {
    event.preventDefault();

    this.props.onSubmit(this.state.term);
  };
  onInputChange(event){
    console.log(this.state.term);
  }

  render() {
    console.log(this.state.term);
    return (
      <div>
        <p>Feedback</p>
      <Form>
        <div className="ui segment">
          <Form.Input fluid placeholder='First name'/>
          <Form.Input fluid placeholder='Last name'/>
          <Form.Input fluid placeholder='john@example.com'/>
          <Form.Field control={TextArea} placeholder='Message'  onChange={e => this.setState({term: e.target.value})} maxLength={500} />
          <p>{this.state.term.length}/500</p>
        </div>
      </Form>
      </div>
    );
  }
}

export default SearchBar;
