import React from 'react';
import { Button, Checkbox, Form, Input, Radio, Select, TextArea } from 'semantic-ui-react'


const formValid = formErrors => {
  let valid = true;

  // make sure everything in the form is filled!
  Object.values(formErrors).forEach(val => {
    val.length > 0 && (valid = false);
  });


  return valid;
};

class SearchBar extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      firstName: null,
      lastName: null,
      email: null,
      message: "",
      formErrors: {
        firstName: "",
        lastName: "",
        email: "",
        message: ""
      }
    };
  }

  onFormSubmit = e => {
    e.preventDefault();

    this.props.onSubmit(this.state);
  };

  onInputChange = e =>{
    e.preventDefault();
    const name = e.target.name;
    const value = e.target.value;
    let formErrors = { ...this.state.formErrors };
    switch (name) {
      case "firstName":
        formErrors.firstName =
          value.length < 3 ? "minimum 3 characaters required" : "";
        break;
      case "lastName":
        formErrors.lastName =
          value.length < 3 ? "minimum 3 characaters required" : "";
        break;
      case "password":
        formErrors.password =
          value.length < 6 ? "minimum 6 characaters required" : "";
        break;
      default:
        break;
    console.log(formErrors);
    }
    this.setState({formErrors, [name]: value});
  }

  render() {
    console.log(this.state);
    return (
      <div className = "container">
        <Form onSubmit = {this.onFormSubmit}>
          <div className="ui segment">
            <p className = "title">Feedback</p>
            <Form.Input name = "firstName" fluid placeholder='First name' onChange={this.onInputChange}/>
            <Form.Input name = "lastName" fluid placeholder='Last name'onChange={this.onInputChange}/>
            <Form.Input name = "email" fluid placeholder='john@example.com' onChange={this.onInputChange}/>
            <Form.Field name = "message" control={TextArea} placeholder='Message'  onChange={this.onInputChange} maxLength={500} />
            <p>{this.state.message.length}/500</p>
            <button className="ui button">Click Here</button>
          </div>
        </Form>

      </div>
    );
  }
}

export default SearchBar;
