import React from 'react';
import { Form, TextArea, Message } from 'semantic-ui-react'

const checkValid = formInputs => {
  var valid = true;

  // make sure everything in the form is filled!
  Object.values(formInputs).forEach(val => {
    if (val === null || val.length === 0){
      valid = false;
    }
  });

  var email = formInputs['email'];
  if (email != null){
    var specialIndex = email.indexOf("@");
    if (specialIndex === -1 || specialIndex === 0 || email.length - 1 <= specialIndex){
      valid = false;
    }
  }
  return valid;

};

class SearchBar extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      firstName: null,
      lastName: null,
      email: null,
      message: "",
      correct: 0 //0 represents the mull stage
    };
  }

  onFormSubmit = e => {
    e.preventDefault();
    if (checkValid(this.state)) {
      this.setState({ correct: 1 }); //1 represents it submitted it correctly!
      this.props.onSubmit(this.state);
    } 
    else {
      this.setState({ correct: 2 }); //2 represents it didn't submit correctly!
    }
  };

  onInputChange = e =>{
    e.preventDefault();
    const name = e.target.name;
    const value = e.target.value;
    this.setState({ [name]: value });
  }

  render() {
    return (
      <div className = "container">
        <Form onSubmit = { this.onFormSubmit }>
          <div className="ui segment">
            <p className = "title">Feedback</p>
            <Form.Input name = "firstName" fluid placeholder='First name' onChange={this.onInputChange}/>
            <Form.Input name = "lastName" fluid placeholder='Last name'onChange={this.onInputChange}/>
            <Form.Input name = "email" fluid placeholder='john@example.com' onChange={this.onInputChange}/>
            <Form.Field name = "message" control={ TextArea } placeholder='Message'  onChange={this.onInputChange} maxLength={500} />
            <p>{ this.state.message.length }/500</p>
            <button className="ui button">Click Here</button>
            {this.state.correct === 2 && 
            <Message
                warning
                header='Form did not submit!'
                content='Make sure to check that all the inputs are filled and that the email is formatted correctly! '
            /> }
            {this.state.correct === 1 && 
            <Message
            success
            header='Form submitted successfully!'
            content='All the info was submitted to the database correctly! Good job! '
          />
            }

          </div>
        </Form>
        <div className="ui segment">
        <form action="/action_page.php" autocomplete="on">
  <input type="text" name="firstName" placeholder="First name"/><br/>
  <input type="text" name="lastName" placeholder="Last name"/><br/>
  <input type="email" name="email" placeholder="email"/><br/>
  <input type="text" name="message" placeholder="message"/><br/>
  <input type="submit"/>
</form>
</div>
      </div>
    );
  }
}

export default SearchBar;
