import React from 'react';
import { Form, TextArea, Message } from 'semantic-ui-react'


class SearchBar extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      firstName: null,
      lastName: null,
      email: null,
      message: "",
      correct: 0 //0 represents the mull stage
    };
  }

  onFormSubmit = e => {
    e.preventDefault();
    this.setState({ correct: 1 }); //1 represents it submitted it correctly!
    this.props.onSubmit(this.state);
  };

  onInputChange = e =>{
    e.preventDefault();
    const name = e.target.name;
    const value = e.target.value;
    this.setState({ [name]: value });
  }

  render() {
    return (
      <div className = "container">
        <Form onSubmit = { this.onFormSubmit }>
          <div className="ui segment">
            <p className = "title">Feedback</p>
            <Form.Input name = "firstName" fluid placeholder='First name' type = "text" required onChange={this.onInputChange}/>
            <Form.Input name = "lastName" fluid placeholder='Last name'type = "text" required onChange={this.onInputChange}/>
            <Form.Input name = "email" fluid placeholder='john@example.com' type = "email" required onChange={this.onInputChange}/>
            <Form.Field name = "message" control={ TextArea } placeholder='Message' type = "text" required onChange={this.onInputChange} maxLength={500} />
            <p className = "count">{ this.state.message.length }/500</p>
            <button className="ui button submitBtn">Submit</button>
            {this.state.correct === 2 && 
            <Message
                warning
                header='Form did not submit!'
                content='Make sure to check that all the inputs are filled and that the email is formatted correctly! '
            /> }
            {this.state.correct === 1 && 
            <Message
            success
            header='Form submitted successfully!'
            content='All the info was submitted to the database correctly! Good job! '
          />
            }
          </div>
        </Form>
      </div>
    );
  }
}

export default SearchBar;
